var check_html_theme=(()=>{let a=`html-theme`,b=`theme`;if(document.getElementById(a).className==`null`||``){if(window.matchMedia&&window.matchMedia(`(prefers-color-scheme: dark)`).matches){console.log(`Dark mode is preferred by the user.`);localStorage.setItem(b,`dark`);document.getElementById(a).className=theme}else{console.log(`Light mode is preferred by the user.`);localStorage.setItem(b,`light`);document.getElementById(a).className=theme}}});try{var theme=localStorage.getItem(`theme`);document.getElementById(`html-theme`).className=theme;if(theme==null){check_html_theme()}}catch(a){console.log(`Operation: Failed`,a)}